INSERT INTO `service_steps` (`id`, `content_ar`, `content_en`, `photo`, `service_id`, `created_at`, `updated_at`) VALUES
(4, '<li class=\"pt-3 p-decerption\">\r\nتحديد الأهداف \r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nإعداد خريطة الموقع (Sitemap)\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nتصميم واجهة المستخدم (UI Design)\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nتصميم تجربة المستخدم (UX Design)\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nتطوير الموقع\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nإضافة المحتوى\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nاختبار وتحسين\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nنشر الموقع\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nالصيانة والتحديث', '<li class=\"pt-3 p-decerption\">\r\nDefine goals\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nCreate a sitemap\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nDesign the user interface (UI)\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nDesign the user experience (UX)\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nDevelop the website\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nAdd content\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nTest and improve\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nLaunch the website\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nMaintain and update\r\n</li>', '1702819060.png', 8, '2023-12-17 12:15:24', '2023-12-17 13:50:58'),
(5, '<li class=\"pt-3 p-decerption\">\r\nالتحليل والبحث\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nوضع خريطة الموقع (Sitemap)\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nتصميم الأسلايدز (Wireframing)\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nتصميم الواجهة الرسومية (UI Design)\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nتطوير النموذج الأولي (Prototype)\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nتطوير التطبيق (App Development)\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nاختبار وتحسين\r\n</li>', '<li class=\"pt-3 p-decerption\">\r\nAnalysis and research\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nSitemap\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nWireframing\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nUI Design\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nPrototype development\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nApp development\r\n</li>\r\n<li class=\"pt-3 p-decerption\">\r\nTesting and improvemen\r\n</li>', '1702819038.png', 9, '2023-12-17 12:17:18', '2023-12-17 13:53:41');

