--
-- Table structure for table `why_us`
--

CREATE TABLE `why_us` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `title_ar` varchar(255) DEFAULT NULL,
  `title_en` varchar(255) DEFAULT NULL,
  `content_ar` text DEFAULT NULL,
  `content_en` text DEFAULT NULL,
  `photo` text DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `why_us`
--

INSERT INTO `why_us` (`id`, `title_ar`, `title_en`, `content_ar`, `content_en`, `photo`, `created_at`, `updated_at`) VALUES
(1, 'أحدث التقنيات الخدمية السحابية', 'Use Technologies', 'نستخدم أحدث التقنيات السحابية لتطوير وتقديم حلولنا، كما نتبع أفضل الممارسات ونواكب التغيرات السريعة في السوق المحلية والعالمية لضمان تقديم تقنيات متقدمة وموثوقة.', 'We utilize the latest cloud technologies to develop and deliver our solutions. We follow best practices and keep up with rapid changes in the local and global market to ensure the delivery of advanced and reliable technologies.', '1702569025.webp', '2023-12-13 12:03:57', '2023-12-14 14:50:25'),
(2, 'تكامل وربط الأنظمة المتنوعة', 'Integration Diverse Systems', 'لدينا القدرة على ربط أنظمتنا مع المنصات المختلفة، سواء كانت حكومية أو خاصة، وهذا يسمح للعملاء بالاستفادة من تكامل سلس كما يسهل تبادل المعلومات بين الأنظمة.', 'We can integrate our systems with various platforms, whether governmental or private. This allows customers to benefit from seamless integration and facilitates the exchange of information between systems.', '1702569045.webp', '2023-12-13 12:03:57', '2023-12-14 14:50:45'),
(3, 'خدمات برمجية متنوعة', 'Diverse Software Services', 'نقدم نظامًا محاسبيًا ERP يتميز بمرونته وقدرته على أن يكون متعدد المهام، وبذلك يمكن للعملاء الاستفادة من مجموعة واسعة من الخدمات البرمجية المتنوعة التي تلبي احتياجاتهم الفريدة.', 'We provide an ERP system that is flexible and capable of multitasking, allowing customers to benefit from a wide range of diverse software services that meet their unique needs.', '1702569081.webp', '2023-12-13 12:03:57', '2023-12-14 14:51:21'),
(4, 'أمان', 'Security', 'نحرص على تأمين خدماتنا بأعلى مستويات الأمان والحماية السيبرانية، كما نتبع معايير صارمة لضمان أمان بيانات العملاء واستقرار النظام.', 'We prioritize securing our services with the highest levels of security and protection. We follow strict standards to ensure the security of customer data and system stability.', '1702569063.webp', '2023-12-13 12:03:57', '2023-12-14 14:51:03'),
(5, 'الإلتزام بمعايير جودة الخدمة', 'Commitment to Quality Standards', 'نحن نضع معايير جودة عالية لخدمتنا ونسعى جاهدين لتحسين أعمالنا بشكل مستمر، كما نستمع إلى آراء عملائنا ونعمل بنشاط على تلبية احتياجاتهم وتوفير حلول مبتكرة وموثوقة.', 'We set high-quality standards for our service and continuously strive to improve our operations. We actively listen to our customers\' feedback and work diligently to meet their needs and provide innovative and reliable solutions.', '1702569142.webp', '2023-12-13 12:03:57', '2023-12-14 14:52:22'),
(6, 'فريق عمل ماهر', 'Skilled Team', 'يتميز فريقنا بالمهارات القوية في التعامل مع تحديات تطوير الأنظمة المحاسبية المعقدة، نحن نضمن توفير فريق متكامل يضم مبرمجين ومحللين محترفين يتفاعلون بشكل مثالي لتطوير وتنفيذ حلول ERP مخصصة وفقًا لمتطلبات العملاء.', 'Our team possesses strong skills in dealing with the challenges of developing complex accounting systems. We ensure the provision of an integrated team consisting of professional programmers and analysts who interact perfectly to develop customized ERP solutions according to customer requirements.', '1702569172.webp', '2023-12-13 12:03:57', '2023-12-14 14:52:52');

